INSERT INTO `tblmedhistory` (`MedHistoryID`, `s_org_extID`, `Surgeries`, `SmokingBefore`, `SmokingDuring`, `Alcochol`, `TrasfusionsYN`, `TrasfusionsReactions`, `AllergiesYN`, `AllergiesToMed`, `AllergiesOther`, `FamilyHistory`, `Weight`, `Height`, `BMI`, `Comments`, `Created`, `CreatedBy`, `LastUpdated`, `LastUpdatedBy`, `IsActive`) VALUES
(141, 211, '1) βασικοκυτταρικό καρκίνωμα στην πλάτη 2015 2) αφαίρεση αδενοειδών εκβλαστήσεων σε παιδική ηλικία', '3-4 τσιγάρα ', 'ΟΧΙ', '', 0, '', 0, '', '', '1) μητέρα έμφραγμα 2) Ca οισοφάγου ο πατέρας', 53, 1.63, 19.948, '-κύηση από Χείλαρη, που μου ήρθε τον Αύγουστο, γιατί έλειπε σε άδεια\r\n-ξεκίνησε στην εγκυμοσύνη να λαμβάνει: 1) T4 50μg 1*1 2) tab Glucophage 1000mg 1/2*3. Της τα έδωσε ενδοκρινολόγος λόγω χαμηλού σακχάρου σε όλες τις μετρήσεις μίας καμπύλης, που είχε κάνει πρόσφατα και λόγω του ότι θέλει την TSH μεταξύ 2-2,5\r\n-μιλάμε με το ζευγάρι στον ενικό\r\n-Clexane 0.4 1*1 και Salospir 80mg 1*1 λόγω ετεροζυγωτίας MTHFR. Είχε δύο ΑΕ (Ιούνιος 2015 και Φεβρουάριος 2016)', '2016-08-11 19:13:53', 'stelios77', '2016-08-16 17:37:58', 'stelios77', 1),
(142, 212, '', '15 τσιγάρα την ημέρα', '', '', 0, '', 0, '', '', '', 53, 1.73, 17.709, '-τη φωνάζουν Πένυ. Αλληλοσυμπαθηθήκαμε και μιλάμε στον ενικό\r\n-είναι ασθενής του Θεολόγου Δημητριάδη και την είδα τον Αύγουστο 2016, που έλειπε σε άδεια\r\n-είναι νοσηλεύτρια στο ΙΑΣΩ Παίδων. Με συμπάθησε και είναι πολύ καλό κορίτσι και πολύ καλό παιδί και ο άντρας της', '2016-08-16 22:17:10', 'stelios77', '2016-08-26 17:47:03', 'stelios77', 1),
(143, 214, '1) αμυγδαλεκτομή σε ηλικία 4 ετών', 'ΟΧΙ', '', '', 0, '', 0, '', '', '', 0, 0, 0, '-είναι ασθενής του Χείλαρη, που την είδα τον Αύγουστο, που έλειπε σε άδεια\r\n-έχει ήδη ένα κοριτσάκι 3,5 ετών\r\n-μιλάμε στον ενικό. Τη φωνάζουν Λένια', '2016-08-26 18:29:52', 'stelios77', '2016-08-26 19:21:36', 'stelios77', 1),
(144, 215, '', '', '', '', 0, '', 0, '', '', '', 0, 0, 0, '-είναι ασθενής του Χείλαρη. Τη γνώρισα και την ξεγέννησα στο ΙΑΣΩ τον Αύγουστο, που έλειπε με άδεια\r\n-στις 40+3 γεννήσαμε ένα υγιέστατο κοριτσάκι, ΣΒ: 3720 γραμμάρια. Τη βάφτισαν Μυρτώ\r\n-μιλάμε στον ενικό, έχουμε γίνει πολύ καλοί φίλοι\r\n-ο Παύλος είναι φαρμακοποιός στον Λευκό Σταυρό και θα ανοίξει δικό του φαρμακείο σύντομα, πολύ κοντά στο ιατρείο μου, στην Παπαδιαμαντοπούλου, δίπλα στο θέατρο Ιλίσια\r\n-η Μαρίτα είναι δικηγόρος, οπότε να την έχω στα υπόψιν και για δικές μου υποθέσεις\r\n-στις 30 Ιανουαρίου 2018 γεννήσαμε με φτ στο ΙΑΣΩ στις 13:36 ένα άρρεν 3280. Όλα καλά. Είναι το δεύτερο παιδάκι της. Την παρακολουθούσε ο Γιώργος σε αυτή την κύηση, αλλά την ημέρα εκείνη είχε τοκετό στο ΜΗΤΕΡΑ και έτσι έτυχε και της ξεγέννησα εγώ και το δεύτερο παιδάκι της', '2016-08-27 11:04:27', 'stelios77', '2018-01-30 16:02:01', 'stelios77', 1),
